$(document).ready(function() {
  var EMAIL_KEY, emailId;
  EMAIL_KEY = 'emailId';
  emailId = localStorage.getItem(EMAIL_KEY);
  if (emailId) {
    $.ajax({
      type: "GET",
      url: serverHost + "/checkUser/" + emailId,
      dataType: "json"
    }).done(function(respData) {
      if (respData.status === 'SUCCESS') {
        window.location.hash = "#home";
        if (respData.popupflag === true) {
          $(".usagewarning").popup("open");
        }
      } else {
        $('#login').show();
      }
    });
  } else {
    $('#login').show();
  }
  return $("#registrationForm").on("submit", function($event) {
    var email, mobileno, postData, size, usrname;
    $event.preventDefault();
    usrname = $(".usrname").val();
    email = $(".email").val();
    mobileno = $(".mobileno").val();
    size = $(".peopleinhome").val();
    postData = {
      residentName: usrname,
      emailId: email,
      mobileNumber: mobileno,
      peopleInHome: size
    };
    $.ajax({
      type: "POST",
      url: serverHost + "/createUser",
      crossDomain: true,
      data: postData,
      dataType: "json"
    }).done(function(respData) {
      if (respData.status === 'SUCCESS') {
        localStorage.setItem(EMAIL_KEY, email);
        window.location.hash = "#home";
      }
    }).fail(function() {});
    return false;
  });
});

